<style>
  #name {
    scale: 1.38;
  }

  #name path {
    animation: line-anim 3000ms ease-in forwards var(--anim-delay),
      stay-hidden var(--anim-delay);
    stroke-dasharray: 0;
    stroke-dashoffset: 0;
    transform-origin: center;
    transition: transform 300ms;
  }

  @keyframes line-anim {
    from {
      stroke-dasharray: var(--total-length);
      stroke-dashoffset: var(--total-length);
    }
    99% {
      stroke-dasharray: var(--total-length);
      stroke-dashoffset: 0;
    }
    to {
      stroke-dasharray: 0;
      stroke-dashoffset: 0;
    }
  }

  @keyframes stay-hidden {
    from {
      visibility: hidden;
    }
    to {
      visibility: hidden;
    }
  }
</style>
